import React, { useState } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';
import { withRouter } from 'react-router-dom';

function ConnexionApp({ history }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:8000/api/login', { username, password });
      localStorage.setItem('token', response.data.token);
      const decodedToken = jwt_decode(response.data.token);

      if (decodedToken.admin === 1) {
        history.push('/admin');
      } else {
        history.push('/homeutilisateur');
      }
    } catch (error) {
      setError('Username ou mot de passe incorrect');
    }
  };

  return (
    <div className="container">
      {localStorage.getItem('token') ? (
        <div>
          Vous êtes déjà connecté
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="mt-3">
          <h1 className="h3 mb-3 font-weight-normal">Connexion</h1>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input type="text" id="username" value={username} onChange={(event) => setUsername(event.target.value)} className="form-control" />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input type="password" id="password" value={password} onChange={(event) => setPassword(event.target.value)} className="form-control" />
          </div>
          {error && <div className="alert alert-danger">{error}</div>}
          <button type="submit" className="btn btn-primary">Connexion</button>
        </form>
      )}
    </div>
  );
}

export default withRouter(ConnexionApp);
