import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import ConnexionApp from './connexion/ConnexionApp.js';
import HomeApp from './HomeApp.js';
import AboutApp from './AboutApp.js';
import HomeUtilisateur from './utilisateur/HomeUtilisateur';
import HomeAdmin from './admin/HomeAdmin';
import DeconnexionApp from './connexion/DeconnexionApp.js';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Vérifie si l'utilisateur est connecté en vérifiant la présence du token
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">Navbar</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup"
            aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
            <div className="navbar-nav">
              <Link className="nav-link" to="/">Home</Link >
              <Link className="nav-link" to="/about">About</Link >
              {isLoggedIn ? (
                <>
                  <Link className="nav-link" to="/compte">Compte</Link >
                  <Link className="nav-link" to="/sorties">Sorties</Link >
                  <DeconnexionApp onLogout={handleLogout} />
                </>
              ) : (
                <Link className="nav-link" to="/connexion">Connexion</Link >
              )}
            </div>
          </div>
        </div>
      </nav>
      <Routes>
        <Route exact path="/" element={<HomeApp />} />
        <Route path="/about" element={<AboutApp />} />
        <Route path="/homeutilisateur" element={<HomeUtilisateur />} />
        <Route path="/admin" element={<HomeAdmin />} />
        <Route path="/connexion" element={<ConnexionApp onLogin={() => setIsLoggedIn(true)} />} />
      </Routes>
    </Router>
  );
}

export default App;
