import React from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom'
import ConnexionApp from './connexion/ConnexionApp.js';
import HomeApp from './HomeApp.js';
import AboutApp from './AboutApp.js';
import HomeUtilisateur from './utilisateur/HomeUtilisateur';
import HomeAdmin from './admin/HomeAdmin';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isAdmin: false, // Par défaut, l'utilisateur n'est pas un administrateur
    };
  }

  handleLogin = (isAdmin) => {
    this.setState({ isAdmin: isAdmin });
  };

  render() {
    const { isAdmin } = this.state;

    return (
      <Router>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">
              Navbar
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNavAltMarkup"
              aria-controls="navbarNavAltMarkup"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
              <div className="navbar-nav">
                <Link className="nav-link" to="/">
                  Home
                </Link>
                <Link className="nav-link" to="/about">
                  About
                </Link>

                {isAdmin ? (
                  <>
                    <Link className="nav-link" to="/accounts">
                      Comptes
                    </Link>
                    <Link className="nav-link" to="/admin">
                      Administration
                    </Link>
                  </>
                ) : (
                  <Link className="nav-link" to="/connexion">
                    Connexion
                  </Link>
                )}

              </div>
            </div>
          </div>
        </nav>
        <Routes>
          <Route exact path="/" element={<HomeApp />} />
          <Route path="/about" element={<AboutApp />} />
          <Route path="/homeutilisateur" element={<HomeUtilisateur />} />
          <Route path="/accounts" element={<HomeUtilisateur />} />
          <Route path="/admin" element={<HomeAdmin />} />
          <Route
            path="/connexion"
            element={<ConnexionApp onLogin={this.handleLogin} />}
          />
        </Routes>
      </Router>
    );
  }
}

export default App;
