import React, { useState } from 'react';
import axios from 'axios';
import jwt_decode from 'jwt-decode';


import HomeUtilisateur from '../utilisateur/HomeUtilisateur.js';
import HomeAdmin from '../admin/HomeAdmin';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isAdmin, setIsAdmin] = useState(false); // Ajout de l'état isAdmin

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:8000/api/login', { username, password });
      localStorage.setItem('token', response.data.token);

      // Décoder le token pour obtenir les informations utilisateur
      const decodedToken = jwt_decode(response.data.token);
      setIsAdmin(decodedToken.admin === 1); // Définir l'état isAdmin en fonction de la valeur de l'attribut "admin" dans le token

      // Afficher un message de succès
      alert("Bienvenue !");
    } catch (error) {
      // Afficher un message d'erreur
      alert("Username ou mot de passe est incorrect!");
    }
  };

  // Utiliser l'opérateur ternaire pour afficher le composant approprié en fonction de la valeur de isAdmin
  return (
    <div className="container">
      {isAdmin ? <HomeAdmin /> : <HomeUtilisateur />}
      <form onSubmit={handleSubmit} className="p-3 border rounded">
        <div className="mb-3">
          <label htmlFor="username" className="form-label">Username:</label>
          <input type="text" className="form-control" id="username" value={username} onChange={(event) => setUsername(event.target.value)} />
        </div>
        <div className="mb-3">
          <label htmlFor="password" className="form-label">Password:</label>
          <input type="password" className="form-control" id="password" value={password} onChange={(event) => setPassword(event.target.value)} />
        </div>
        <button type="submit" className="btn btn-primary">Login</button>
      </form>
    </div>
  );
}

export default App;
