import React, { Component } from 'react';
import axios from 'axios';

class UserList extends Component {
  state = {
    userList: [],
    loading: true,
    error: null,
  };

  componentDidMount() {
    const token = localStorage.getItem('token');
    if (!token) {
      // Rediriger l'utilisateur vers la page de connexion s'il n'y a pas de token
      this.props.history.push('/login');
      return;
    }

    axios.get('/api/users', { headers: { Authorization: `Bearer ${token}` } })
      .then((response) => {
        this.setState({ userList: response.data, loading: false });
      })
      .catch((error) => {
        this.setState({ error: error.response.data.message, loading: false });
      });
  }

  render() {
    const { userList, loading, error } = this.state;

    if (loading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>Error: {error}</div>;
    }

    return (
      <div>
        <h1>User List</h1>
        <ul>
          <li>Jeton est valide </li>
        </ul>
      </div>
    );
  }
}

export default UserList;
